import {Component, Input} from '@angular/core';
import {FormControl} from "@angular/forms";

@Component({
  selector: 'app-text-field',
  templateUrl: './text-field.component.html',
  styleUrls: ['./text-field.component.css']
})
export class TextFieldComponent {
  @Input() fieldName: string;
  @Input() required: boolean;
  @Input() nextLine: boolean;
  @Input() formElement: FormControl;
}
<div class="pt-3 mr-4">
  <app-field-name
    [fieldName]="fieldName"
    [required]="required"
  ></app-field-name>

  <div *ngIf="nextLine" class="block"
  >
    <input
      type="text"
      class="border rounded-1 w-full py-2 px-3 mt-1"
      [formControl]="formElement"
    >
  </div>
  <div *ngIf="!nextLine" class="inline-block ml-2"
  >
    <input
      type="text"
      class="border rounded-1 w-auto py-2 px-3 mt-1 ml-3"
      [formControl]="formElement"
    >
  </div>

  <div *ngIf="formElement.touched"
  >
    <p class="text-red-600 font-normal mb-2"
       *ngIf="formElement.hasError('minLength') && formElement.errors?.minLength"
    >Слишком мало символов</p>

    <p class="text-red-600 font-normal mb-2"
       *ngIf="formElement.hasError('maxLength') && formElement.errors?.maxLength"
    >Слишком много символов</p>
  </div>

</div>
