import {Component, Input} from '@angular/core';
import {Router} from "@angular/router";
import {UrlService} from "../../service/url.service";

@Component({
  selector: 'app-large-area-with-action-buttons',
  templateUrl: './large-area-with-action-buttons.component.html',
  styleUrls: ['./large-area-with-action-buttons.component.css']
})
export class LargeAreaWithActionButtonsComponent {
  @Input() title: string;
  @Input() itemToPerformActionsWith: Object;
  @Input() buttonBackRoute: [string] | [string, bigint];

  private router: Router;
  private urlService: UrlService;

  constructor(router: Router,
              urlService: UrlService) {
    this.router = router;
    this.urlService = urlService;
  }

  navigateBack() {
    this.router.navigate(this.buttonBackRoute);
  }
}
<div
  class="large-window"
>
  <div class="row">
    <div class="col-auto">
      <button *ngIf="buttonBackRoute"
              type="submit"
              class="button-back-arrow"
              (click)="navigateBack()"
      >ü†î</button>
    </div>
    <div class="col">
      <h1 class="text-center fs-5">{{title | titlecase}}</h1>
    </div>
    <div class="col-auto">
      <div class="inline-flex flex-row gap-x-2">
        <app-button-edit
          [editable]="itemToPerformActionsWith"
        ></app-button-edit>
        <app-button-delete
          [deletable]="itemToPerformActionsWith"
        ></app-button-delete>
      </div>
    </div>
  </div>
  <app-dark-h-line></app-dark-h-line>
  <ng-content></ng-content>
</div>
