import {Component, EventEmitter, Input, Output} from '@angular/core';
import {INamed} from "../../additional/INamed";
import {FormControl} from "@angular/forms";

@Component({
  selector: 'app-combobox-group',
  templateUrl: './combobox-group.component.html',
  styleUrls: ['./combobox-group.component.css']
})
export class ComboboxGroupComponent {
  @Input() label: string;
  @Input() required: boolean;
  @Input() objects: INamed[];

  @Output() itemSelection = new EventEmitter<INamed>();

  selectionChanged(item: INamed) {
    this.itemSelection.emit(item);
  }

  getLegendText(): string {
    return this.label + (this.required ? ' *' : '');
  }
}
<fieldset class="border-1 border-gray-500 rounded block">
  <legend class="text-black text-md-start">
    {{getLegendText()}}
  </legend>

  <div class="flex items-center flex-row gap-x-4"
    *ngFor="let item of objects"
  >
    <input
      *ngFor="let item of objects"
      type="radio"
      name="{{label}}"
      value="{{item.getName()}}"
      (select)="selectionChanged(item)"
    /><span class="text-black">{{item.getName()}}</span>
  </div>
</fieldset>
