import {Component, Input} from '@angular/core'
import {User} from "../../entity/User";

@Component({
  selector: 'app-user-profile',
  templateUrl: './user_profile.component.html',
  styleUrls: ['./user_profile.component.css']
})
export class UserProfileComponent {
  @Input() user: User;
}
<div class="py-4 px-6 border rounded flex items-center flex-row mb-2">
  <h2 class="mb-2 text-lg text-center">{{user.firstName}} {{user.lastName}}</h2>
</div>
