@Component({
  selector: 'app-horizontal-line',
  templateUrl: './horizontal-line.component.html',
  styleUrls: ['./horizontal-line.component.css']
})
export class HorizontalLineComponent {
  @Input() colorName: string;
}
<svg class="stroke-current inline-block h-2 w-full" viewBox="0 0 300 9" preserveAspectRatio="none"
     xmlns="http://www.w3.org/2000/svg" fill="none" stroke-width="1">
  <path [ngStyle]="{'color': colorName}" d="M1 1 H400"/>
</svg>
