import {Component, OnInit} from '@angular/core';
import {FormControl, FormGroup, Validators} from "@angular/forms";
import {Gender} from "../../entity/Gender";
import {UserService} from "../../service/users.service";

@Component({
  selector: 'app-create-user',
  templateUrl: './create-user.component.html',
  styleUrls: ['./create-user.component.css']
})
export class CreateUserComponent implements OnInit {
  private userService: UserService;
  showAllErrors = false;

  form = new FormGroup({
    email: new FormControl<string>('', [
      Validators.email,
      Validators.minLength(5),
      Validators.maxLength(60),
      Validators.required
    ]),
    password: new FormControl<string>('', [
      Validators.minLength(8),
      Validators.maxLength(30),
      Validators.required
    ]),
    confirmPassword: new FormControl<string>('', [
      Validators.minLength(8),
      Validators.maxLength(30),
      Validators.required
    ]),
    firstName: new FormControl<string>('', [
      Validators.minLength(2),
      Validators.maxLength(30),
      Validators.required
    ]),
    lastName: new FormControl<string>('', [
      Validators.minLength(2),
      Validators.maxLength(30),
      Validators.required
    ]),
    phoneNumber: new FormControl<string>('', [
      Validators.pattern('\\d{3,15}')
    ]),
    gender: new FormControl<Gender>(null, [
      Validators.required
    ])
  })

  constructor(userService: UserService) {
    this.userService = userService;
  }

  ngOnInit() {
  }

  submit() {
    if (this.isAnyErrorInFields()) {
      this.showAllErrors = true;
      return;
    }
    this.userService.register(user, password);
  }

  isAnyErrorInFields(): boolean {
    return this.doesControlHaveError(this.form.controls.email) ||
      this.doesControlHaveError(this.form.controls.password) ||
      this.doesControlHaveError(this.form.controls.confirmPassword) ||
      this.doesControlHaveError(this.form.controls.firstName) ||
      this.doesControlHaveError(this.form.controls.lastName) ||
      this.doesControlHaveError(this.form.controls.phoneNumber) ||
      this.doesControlHaveError(this.form.controls.gender);
  }

  doesControlHaveError(formControl: FormControl): boolean {
    return formControl.errors != null;
  }
}
<form [formGroup]="form" (ngSubmit)="submit()">
  <app-text-field
    fieldName="Email"
    [nextLine]="true"
    [formElement]="form.controls.email"
    [showError]="showAllErrors"
  ></app-text-field>

  <app-text-field
    fieldName="Пароль"
    [nextLine]="true"
    [formElement]="form.controls.password"
    [showError]="showAllErrors"
  ></app-text-field>

  <app-text-field
    fieldName="Подтверждение пароля"
    [nextLine]="true"
    [formElement]="form.controls.confirmPassword"
    [showError]="showAllErrors"
  ></app-text-field>

  <app-text-field
    fieldName="Имя"
    [nextLine]="true"
    [formElement]="form.controls.firstName"
    [showError]="showAllErrors"
  ></app-text-field>

  <app-text-field
    fieldName="Фамилия"
    [nextLine]="true"
    [formElement]="form.controls.lastName"
    [showError]="showAllErrors"
  ></app-text-field>

  <app-text-field
    fieldName="Номер телефона"
    [nextLine]="true"
    [formElement]="form.controls.phoneNumber"
    [showError]="showAllErrors"
  ></app-text-field>

  <app-choice-gender
    fieldName="Пол"
    [formElement]="form.controls.gender"
    [showError]="showAllErrors"
  ></app-choice-gender>

  <button
    type="submit"
    class="py-2 px-4 mt-3 mx-auto rounded-1 bg-blue-600 text-white"
  >Создать</button>
</form>
