import {Component, Input} from '@angular/core';
import {ModalService} from "../../service/modal.service";

@Component({
  selector: 'app-modal-window',
  templateUrl: './modal-window.html',
  styleUrls: ['./modal-window.css']
})
export class ModalWindow {
  @Input() title: string;

  modalService: ModalService;

  constructor(modalService: ModalService) {
    this.modalService = modalService;
  }
}
<div class="bg-black/50 fixed top-0 right-0 left-0 bottom-0 h-auto"
     (click)="modalService.close()"
></div>

<div
  class="w-[500px] position-fixed rounded p-6 bg-white top-50 start-50 translate-middle"
>
  <h1 class="text-center mb-2 text-lg">{{title | titlecase}}</h1>
  <app-light-h-line></app-light-h-line>
  <ng-content></ng-content>
</div>
