import {Component, Input, OnDestroy, OnInit} from '@angular/core';
import {FieldService} from "../../service/field.service";
import {FormService} from "../../service/form.service";
import {Subscription} from "rxjs";
import {Form} from "../../entity/Form";
import {Field} from "../../entity/Field";

@Component({
  selector: 'app-manage-fields',
  templateUrl: './manage-fields.component.html',
  styleUrls: ['./manage-fields.component.css']
})
export class ManageFieldsComponent implements OnInit, OnDestroy {
  @Input() formId: bigint;

  private fieldService: FieldService;
  private formService: FormService;

  form: Form;
  fields: Field[];

  globalError: string = '';

  private subscriptionFields: Subscription;
  private subscriptionForm: Subscription;
  private subscriptionDelete: Subscription;

  constructor(fieldService: FieldService,
              formService: FormService) {
    this.fieldService = fieldService;
    this.formService = formService;
  }

  ngOnInit() {
    this.loadFields();
    this.loadForm();
  }

  ngOnDestroy() {
    if (this.subscriptionFields)
      this.subscriptionFields.unsubscribe();
    if (this.subscriptionForm)
      this.subscriptionForm.unsubscribe();
    if (this.subscriptionDelete)
      this.subscriptionDelete.unsubscribe();
  }

  isGlobalErrorSet(): boolean {
    return this.globalError != '';
  }

  deleteField(field: Field) {
    this.subscriptionDelete = this.fieldService.deleteField(field)
      .subscribe(result => {
        console.log(result);
        if (result.success) {
          this.loadFields();
        }
        else {
          this.globalError = result.message;
        }
      });
  }

  private loadFields() {
    if (this.subscriptionFields)
      this.subscriptionFields.unsubscribe();

    this.subscriptionFields = this.fieldService.getFieldsByFormId(this.formId)
      .subscribe(result => {
        this.fields = result;
      });
  }

  private loadForm() {
    if (this.subscriptionForm)
      this.subscriptionForm.unsubscribe();

    this.subscriptionForm = this.formService.getFormById(this.formId)
      .subscribe(result => {
        this.form = result;
      });
  }
}
<app-error-paragraph
  *ngIf="isGlobalErrorSet()"
  [errorMessage]="globalError"
></app-error-paragraph>

<table class="table-white-gray"
>
  <thead>
  <tr>
    <th class="w-6/12">Вопрос</th>
    <th class="w-3/12">Тип</th>
    <th class="w-auto">Нужно</th>
    <th class="w-auto">Активно</th>
    <th class="w-auto"></th>
  </tr>
  </thead>
  <tbody *ngIf="fields">
  <tr *ngFor="let field of fields; let even = even" [ngClass]="{'bg-gray-100': even}">
    <td>{{field.label}}</td>
    <td>{{field.type.name}}</td>
    <td>{{field.required}}</td>
    <td>{{field.active}}</td>
    <td>
      <div class="action-buttons">
        <app-button-edit
          [editable]="field"
        ></app-button-edit>

        <button class="button-in-table"
                type="submit"
                (click)="deleteField(field)"
        ><span class="btn-icon">✖</span></button>
      </div>
    </td>
  </tr>
  </tbody>
</table>
