import {Component, OnDestroy, OnInit} from '@angular/core';
import {Subscription} from "rxjs";
import {UserService} from "../../service/user.service";

@Component({
  selector: 'app-finish-registration',
  templateUrl: './finish-registration.component.html',
  styleUrls: ['./finish-registration.component.css']
})
export class FinishRegistrationComponent implements OnInit, OnDestroy {
  private userService: UserService;

  successfulReg = false;
  globalError: string = '';

  private subscription: Subscription;

  constructor(userService: UserService) {
    this.userService = userService;
  }

  ngOnInit() {
    this.subscription = this.userService.finishRegistration(this.getCurrentUrl())
      .subscribe(result => {
        this.successfulReg = result.success;
        if (!result.success)
          this.globalError = 'Ошибка при подтверждении регистрации: ' + result.message;
      });
  }

  ngOnDestroy() {
    if (this.subscription)
      this.subscription.unsubscribe();
  }

  isGlobalErrorSet(): boolean {
    return this.globalError != '';
  }

  private getCurrentUrl() {
    return location.href;
  }
}
<div class="main-body">
  <app-header></app-header>
  <app-error></app-error>

  <app-narrow-area
    title="Завершение регистрации"
  >
    <app-error-paragraph
      *ngIf="isGlobalErrorSet()"
      [errorMessage]="globalError"
    ></app-error-paragraph>

    <app-plain-paragraph *ngIf="!successfulReg">
      Идёт завершение регистрации...
    </app-plain-paragraph>

    <app-plain-paragraph *ngIf="successfulReg">
      Поздравляем, ваш аккаунт успешно зарегистрирован!
      Войдите в свой новый аккаунт.
    </app-plain-paragraph>

    <app-middle-control
      *ngIf="successfulReg"
      [paddingTop]="2"
    >
      <button
        class="button-red-very-rounded-narrow"
        routerLink="/login"
      >Войти в аккаунт</button>
    </app-middle-control>
  </app-narrow-area>
</div>
