import { Component } from '@angular/core';
import {ActivatedRoute} from "@angular/router";
import {Subscription} from "rxjs";
import {SessionService} from "../../service/session.service";

@Component({
  selector: 'app-mail-to-restore-sent',
  templateUrl: './mail-to-restore-sent.component.html',
  styleUrls: ['./mail-to-restore-sent.component.css']
})
export class MailToRestoreSentComponent {
  private activatedRoute: ActivatedRoute;
  private sessionService: SessionService;

  email: string;

  private subscription1: Subscription;
  private subscription2: Subscription;

  constructor(activatedRoute: ActivatedRoute,
              sessionService: SessionService) {
    this.activatedRoute = activatedRoute;
    this.sessionService = sessionService;
  }

  ngOnInit() {
    this.subscription2 = this.activatedRoute.url
      .subscribe(result => {
        this.sessionService.saveCurrentPage(result[0].path);
      });

    this.subscription1 = this.activatedRoute.queryParams
      .subscribe(params => {
      this.email = params['email'];
    });
  }

  ngOnDestroy() {
    if (this.subscription1)
      this.subscription1.unsubscribe();
    if (this.subscription2)
      this.subscription2.unsubscribe();
  }
}
<div class="main-body">
  <app-header></app-header>
  <app-error></app-error>

  <app-narrow-area
    title="Восстановление пароля"
  >
    <app-plain-paragraph>
      На вашу почту {{email}} было отправлено письмо с дальнейшими указаниями.
      Надеемся, вы не забыли пароль от своей почты.
    </app-plain-paragraph>
  </app-narrow-area>
</div>
