import { Component } from '@angular/core';
import {SessionService} from "../../service/session.service";
import {ActivatedRoute} from "@angular/router";
import {Subscription} from "rxjs";

@Component({
  selector: 'app-main-page',
  templateUrl: './main-page.component.html',
  styleUrls: ['./main-page.component.css']
})
export class MainPageComponent {
  sessionService: SessionService;
  private activatedRoute: ActivatedRoute;

  private subscription: Subscription;

  constructor(sessionService: SessionService,
              activatedRoute: ActivatedRoute) {
    this.sessionService = sessionService;
    this.activatedRoute = activatedRoute;
  }

  ngOnInit() {
    this.subscription = this.activatedRoute.url
      .subscribe(result => {
        this.sessionService.saveCurrentPage(result[0].path);
      });
  }

  ngOnDestroy() {
    if (this.subscription)
      this.subscription.unsubscribe();
  }
}
<div class="main-body">
  <app-header></app-header>
  <app-error></app-error>

  <app-large-area
    title="Главная страница"
  >
    <app-about-text></app-about-text>

    <app-middle-control *ngIf="!sessionService.isCurrentUserAuthorized()">
        <button
          class="button-red-very-rounded-narrow"
          routerLink="/register"
        >Создать аккаунт</button>
        <button
          class="button-red-very-rounded-narrow"
          routerLink="/login"
        >Войти в аккаунт</button>
    </app-middle-control>
  </app-large-area>
</div>
