import {Component, Input} from '@angular/core'
import {User} from "../../entity/User";

@Component({
  selector: 'app-user-profile',
  templateUrl: './user_profile.component.html',
  styleUrls: ['./user_profile.component.css']
})
export class UserProfileComponent {
  @Input() user: User;
}
<div class="py-2 px-4 border rounded flex items-center flex-row mb-2 gap-x-5">
  <div ng-if="user.gender.name == 'male'">
    <img class="user-profile-image" src="assets/images/user_male_icon.png">
  </div>
  <div ng-if="user.gender.name == 'female'">
    <img class="user-profile-image" src="assets/images/user_female_icon.png">
  </div>
  <h2 class="mb-2 text-lg text-center">
    {{user.firstName}} {{user.lastName}} <span class="font-monospace text-blue-800">({{user.email}})</span>
  </h2>
</div>
