import { Component } from '@angular/core';
import {UserService} from "../../service/users.service";
import {Router} from "@angular/router";
import {FormControl, FormGroup, Validators} from "@angular/forms";

@Component({
  selector: 'app-change-password',
  templateUrl: './change-password.component.html',
  styleUrls: ['./change-password.component.css']
})
export class ChangePasswordComponent {
  private userService: UserService;
  private router: Router;

  showAllErrors = false;
  globalError: string = '';
  private successfulLogin = false;

  form = new FormGroup({
    email: new FormControl<string>('', [
      Validators.email,
      Validators.required
    ]),
    password: new FormControl<string>('', [
      Validators.required
    ])
  })

  constructor(userService: UserService,
              router: Router) {
    this.userService = userService;
    this.router = router;
  }

  ngOnInit() {
  }

  isGlobalErrorSet(): boolean {
    return this.globalError != '';
  }

  submit() {
    if (this.anyErrorExists())
      return;

    this.loginUser();
  }

  private anyErrorExists(): boolean {
    this.resetGlobalError();

    if (this.isAnyErrorInFields()) {
      this.showAllErrors = true;
      return true;
    }
    return false;
  }

  private loginUser() {
    let email = this.getEmailFromField();
    let password = this.getPasswordFromField();

    this.userService.login(email, password)
      .subscribe(result => {
        console.log(result);
        if (result.success) {
          this.successfulLogin = true;
          this.navigateToMainPage();
        }
        else {
          this.globalError = result.message;
          this.successfulLogin = false;
        }
      });
  }

  private navigateToMainPage() {
    this.router.navigate(['/']);
  }

  resetGlobalError() {
    this.globalError = '';
  }

  getEmailFromField(): string {
    return this.form.controls.email.getRawValue();
  }

  getPasswordFromField(): string {
    return this.form.controls.password.getRawValue();
  }

  isAnyErrorInFields(): boolean {
    return this.doesControlHaveError(this.form.controls.email) ||
      this.doesControlHaveError(this.form.controls.password);
  }

  doesControlHaveError(formControl: FormControl): boolean {
    return formControl.errors != null;
  }

  doTransferToMainPage() {
    this.router.navigate(['/']);
  }
}
<form [formGroup]="form" (ngSubmit)="submit()">
  <app-error-paragraph
    *ngIf="isGlobalErrorSet()"
    [errorMessage]="globalError"
  ></app-error-paragraph>

  <app-text-field
    fieldName="Текущий пароль"
    [formElement]="form.controls.email"
    [showError]="showAllErrors"
  ></app-text-field>

  <app-text-field
    fieldName="Новый пароль"
    [formElement]="form.controls.password"
    [showError]="showAllErrors"
  ></app-text-field>

  <app-text-field
    fieldName="Подтвердите новый пароль"
    [formElement]="form.controls.password"
    [showError]="showAllErrors"
  ></app-text-field>

  <br/>

  <button
    type="submit"
    class="button-red-rounded"
  >Изменить</button>

  <button
    class="button-back"
    (click)="doTransferToMainPage()"
  >Назад</button>

</form>
