@Component({
  selector: 'app-multi-line-text',
  templateUrl: './multi-line-text.component.html',
  styleUrls: ['./multi-line-text.component.css']
})
export class MultiLineTextComponent {
  @Input() text: string;
}
<div class="scroll-container">
  <div class="scroll-content">
    {{text}}
  </div>
</div>
