import {Component, Input} from '@angular/core'
import {User} from "../../entity/User";

@Component({
  selector: 'app-user-profile',
  templateUrl: './user_profile.component.html',
  styleUrls: ['./user_profile.component.css']
})
export class UserProfileComponent {
  @Input() user: User;

  isMale() : boolean {
    return this.user.gender.name == 'MALE';
  }

  isFemale() : boolean {
    return this.user.gender.name == 'FEMALE';
  }
}
<div class="py-2 px-4 border rounded flex items-center flex-row mb-2 gap-x-5">
  <!--<div *ngIf="isMale()">
    <img class="user-profile-image" src="assets/images/user_male_icon.png">
  </div>
  <div *ngIf="isFemale()">
    <img class="user-profile-image" src="assets/images/user_female_icon.png">
  </div>-->

  <ng-template [ngIf]="isMale()">
    <img class="user-profile-image" src="assets/images/user_male_icon.png" alt="male">
  </ng-template>
  <ng-template [ngIf]="isFemale()">
    <img class="user-profile-image" src="assets/images/user_female_icon.png" alt="female">
  </ng-template>

  <h2 class="mb-2 text-lg text-center">
    {{user.firstName}} {{user.lastName}} <span class="font-monospace text-blue-800">({{user.email}})</span>
  </h2>
  <p class="font-monospace bg-cyan-100 my-auto">{{user.phoneNumber}}</p>
</div>
