import {Component, OnDestroy, OnInit} from '@angular/core';
import {ActivatedRoute} from "@angular/router";
import {Subscription} from "rxjs";

@Component({
  selector: 'app-finish-registration',
  templateUrl: './finish-registration.component.html',
  styleUrls: ['./finish-registration.component.css']
})
export class FinishRegistrationComponent implements OnInit, OnDestroy {
  private activatedRoute: ActivatedRoute;

  firstName: string;
  lastName: string;

  private subscription: Subscription;
  constructor(activatedRoute: ActivatedRoute) {
    this.activatedRoute = activatedRoute;
  }

  ngOnInit() {
    this.subscription = this.activatedRoute.queryParams.subscribe(params => {
      this.firstName = params['first_name'];
      this.lastName = params['last_name'];
    });
  }

  ngOnDestroy() {
    if (this.subscription)
      this.subscription.unsubscribe();
  }
}
<div class="main-body">
  <app-header></app-header>
  <app-error></app-error>

  <app-narrow-area
    title="Успешная регистрация"
  >
    <app-plain-paragraph>
      {{firstName}} {{lastName}}, ваш аккаунт успешно зарегистрирован!
    </app-plain-paragraph>
    <app-button-back-to-main></app-button-back-to-main>
  </app-narrow-area>
</div>
