import { Component } from '@angular/core';
import {SessionService} from "../../service/session.service";
import {ActivatedRoute} from "@angular/router";
import {Subscription} from "rxjs";

@Component({
  selector: 'app-successful-restoration',
  templateUrl: './successful-restoration.component.html',
  styleUrls: ['./successful-restoration.component.css']
})
export class SuccessfulRestorationComponent {
  private sessionService: SessionService;
  private activatedRoute: ActivatedRoute;

  private subscription: Subscription;

  constructor(sessionService: SessionService,
              activatedRoute: ActivatedRoute) {
    this.sessionService = sessionService;
    this.activatedRoute = activatedRoute;
  }

  ngOnInit() {
    this.subscription = this.activatedRoute.url
      .subscribe(result => {
        this.sessionService.saveCurrentPage(result[0].path);
      });
  }

  ngOnDestroy() {
    if (this.subscription)
      this.subscription.unsubscribe();
  }
}
