import {Component, Input} from '@angular/core';
import {FormControl, Validators} from "@angular/forms";

@Component({
  selector: 'app-text-field',
  templateUrl: './text-field.component.html',
  styleUrls: ['./text-field.component.css']
})
export class TextFieldComponent {
  @Input() fieldName: string;
  @Input() nextLine: boolean = true;
  @Input() formElement: FormControl;
  @Input() showError: boolean = false;
  @Input() additionalNote: string = '';

  isFieldRequired(): boolean {
    return this.formElement.hasValidator(Validators.required);
  }
}
<div class="pt-3 mr-4">
  <app-field-name
    [fieldName]="fieldName"
    [required]="isFieldRequired()"
  ></app-field-name>

  <div *ngIf="nextLine" class="block"
  >
    <input
      type="text"
      class="border rounded-1 w-full py-2 px-3 mt-1"
      [formControl]="formElement"
    >
  </div>
  <div *ngIf="!nextLine" class="inline-block ml-2"
  >
    <input
      type="text"
      class="border rounded-1 w-auto py-2 px-3 mt-1 ml-3"
      [formControl]="formElement"
    >
  </div>

  <app-field-validator
    [formElement]="formElement"
    [showError]="showError"
    [additionalNote]="additionalNote"
  ></app-field-validator>

</div>
