import { Component } from '@angular/core';
import {NavigationExtras, Router} from "@angular/router";
import {Subscription} from "rxjs";
import {FormControl, FormGroup, Validators} from "@angular/forms";
import {MailService} from "../../service/mail.service";

@Component({
  selector: 'app-email-to-restore-password-page',
  templateUrl: './email-to-restore-password-page.component.html',
  styleUrls: ['./email-to-restore-password-page.component.css']
})
export class EmailToRestorePasswordPageComponent {
  private mailService: MailService;
  private router: Router;

  showAllErrors = false;
  globalError: string = '';
  sendingMailProcess = false;

  private subscription: Subscription;

  form = new FormGroup({
    email: new FormControl<string>('', [
      Validators.email,
      Validators.required
    ])
  });

  constructor(mailService: MailService,
              router: Router) {
    this.mailService = mailService;
    this.router = router;
  }

  ngOnDestroy() {
    if (this.subscription)
      this.subscription.unsubscribe();
  }

  isGlobalErrorSet(): boolean {
    return this.globalError != '';
  }

  submit() {
    if (this.anyErrorExists())
      return;

    this.sendMail();
  }

  private anyErrorExists(): boolean {
    this.resetGlobalError();

    if (this.isAnyErrorInFields()) {
      this.showAllErrors = true;
      return true;
    }
    return false;
  }

  private sendMail() {
    let email = this.getEmailFromField();

    this.sendingMailProcess = true;
    this.subscription = this.mailService.sendPasswordRestoration(email)
      .subscribe(result => {
        console.log(result);
        if (result.success) {
          this.sendingMailProcess = false;
          this.navigateToInfoPage(email);
        }
        else {
          this.globalError = result.message;
        }
      });
  }

  private navigateToInfoPage(email: string) {
    let navigationExtras: NavigationExtras = {
      queryParams: {'email': email}
    };
    this.router.navigate(['/mail-to-restore-sent'], navigationExtras);
  }

  private resetGlobalError() {
    this.globalError = '';
  }

  private getEmailFromField(): string {
    return this.form.controls.email.getRawValue();
  }

  private isAnyErrorInFields(): boolean {
    return this.doesControlHaveError(this.form.controls.email);
  }

  private doesControlHaveError(formControl: FormControl): boolean {
    return formControl.errors != null;
  }
}
<div class="main-body">
  <app-header></app-header>
  <app-error></app-error>

  <app-narrow-area
    title="Восстановление пароля"
  >
    <form [formGroup]="form" (ngSubmit)="submit()">
      <app-error-paragraph
        *ngIf="isGlobalErrorSet()"
        [errorMessage]="globalError"
      ></app-error-paragraph>

      <app-middle-control *ngIf="sendingMailProcess">
        <span class="text-blue-500 fs-6 fw-bold">
          Выполняется отправка письма на ваш Email...
        </span>
      </app-middle-control>

      <app-text-field-placeholder
        placeholder="Email"
        [formElement]="form.controls.email"
        [showError]="showAllErrors"
      ></app-text-field-placeholder>

      <app-middle-control
        [paddingTop]="3">
        <button
          type="submit"
          class="button-red-rounded"
        >Отправить письмо на почту</button>
      </app-middle-control>

      <p class="text-center fs-6 pt-3">
        <span class="text-black">Вспомнили пароль? </span>
        <a routerLink="/login" class="blue-bold-link-text">Войти</a>
      </p>

    </form>

  </app-narrow-area>
</div>
