import {Component, Input} from '@angular/core';
import {SessionService} from "../../service/session.service";
import {Router} from "@angular/router";
import {UrlService} from "../../service/url.service";

@Component({
  selector: 'app-large-area',
  templateUrl: './large-area.component.html',
  styleUrls: ['./large-area.component.css']
})
export class LargeAreaComponent {
  @Input() title: string;
  @Input() buttonBackNeeded = false;

  private router: Router;
  private urlService: UrlService;

  constructor(router: Router,
              urlService: UrlService) {
    this.router = router;
    this.urlService = urlService;
  }

  navigateBack() {
    this.router.navigate([this.urlService.getLastPage()]);
  }
}
<div
  class="large-window"
>
  <div class="d-flex align-items-center">
    <div class="mr-4">
      <button *ngIf="buttonBackNeeded"
        type="submit"
        class="button-back-arrow"
        (click)="navigateBack()"
      >ü†î</button>
    </div>
    <div class="flex-fill">
      <h1 class="text-center fs-4">{{title | titlecase}}</h1>
    </div>
  </div>
  <app-dark-h-line></app-dark-h-line>
  <ng-content></ng-content>
</div>
