import {Component, OnInit} from '@angular/core';
import {User} from "../../entity/User";
import {UserService} from "../../service/user.service";

@Component({
  selector: 'app-navigation-menu',
  templateUrl: './navigation-menu.component.html',
  styleUrls: ['./navigation-menu.component.css']
})
export class NavigationMenuComponent implements OnInit {
  private userService: UserService;

  user: User;

  constructor(userService: UserService) {
    this.userService = userService;
  }


  ngOnInit(): void {
    this.userService.getCurrentUser()
      .subscribe(result => {
        this.user = result;
      });
  }

  getUserName(): string {
    if (this.user)
      return this.user.firstName + ' ' + this.user.lastName;
    return 'Мой профиль';
  }
}
<nav class="navigation_menu"
>
  <h1>
    <a routerLink="/" class="text-decoration-none">
      <app-logotype-text></app-logotype-text>
    </a>
  </h1>

  <div class="menu-items-div"
  >
    <a routerLink="/topics" class="menu-item">Каталог</a>
    <a routerLink="/" class="menu-item">Мои формы</a>
    <a routerLink="/user-list" class="menu-item">Пользователи</a>
    <div class="dropdown">
      <button class="dropbtn">{{getUserName()}} ▼</button>
      <div class="dropdown-content">
        <a href="#">Мой профиль</a>
        <a href="#">Сменить пароль</a>
        <a href="#">Выйти</a>
      </div>
    </div>
  </div>
</nav>
