import {Component, OnInit} from '@angular/core';
import {ActivatedRoute} from "@angular/router";
import {Subscription} from "rxjs";
import {SessionService} from "../../service/session.service";

@Component({
  selector: 'app-confirm-registration',
  templateUrl: './confirm-registration.component.html',
  styleUrls: ['./confirm-registration.component.css']
})
export class ConfirmRegistrationComponent implements OnInit {
  private activatedRoute: ActivatedRoute;
  private sessionService: SessionService;

  firstName: string;
  lastName: string;
  email: string;

  private subscription1: Subscription;
  private subscription2: Subscription;

  constructor(activatedRoute: ActivatedRoute,
              sessionService: SessionService) {
    this.activatedRoute = activatedRoute;
    this.sessionService = sessionService;
  }

  ngOnInit() {
    this.subscription1 = this.activatedRoute.queryParams
      .subscribe(params => {
      this.firstName = params['first_name'];
      this.lastName = params['last_name'];
      this.email = params['email'];
    });
  }

  ngOnDestroy() {
    if (this.subscription1)
      this.subscription1.unsubscribe();
  }
}
<div class="main-body">
  <app-header></app-header>
  <app-error></app-error>

  <app-narrow-area
    title="Подтверждение электронной почты"
  >
    <app-plain-paragraph>
      {{firstName}} {{lastName}}, для завершения регистрации вам необходимо зайти
      на вашу почту {{email}} и перейти по ссылке для подтверждения, что указанная почта - ваша.
    </app-plain-paragraph>
  </app-narrow-area>
</div>
