import {Component, OnDestroy, OnInit} from '@angular/core';
import {FormService} from "../../service/form.service";
import {UserService} from "../../service/user.service";
import {Subscription} from "rxjs";
import {Form} from "../../entity/Form";
import {User} from "../../entity/User";
import {Topic} from "../../entity/Topic";
import {TopicService} from "../../service/topic.service";
import {Field} from "../../entity/Field";

@Component({
  selector: 'app-manage-questionnaires',
  templateUrl: './manage-questionnaires.component.html',
  styleUrls: ['./manage-questionnaires.component.css']
})
export class ManageQuestionnairesComponent implements OnInit, OnDestroy {
  private formService: FormService;
  private userService: UserService;
  private topicService: TopicService;

  user: User;
  forms: Form[];
  fields: Field[][];

  private subscriptionUser: Subscription;
  private subscriptionForms: Subscription;

  constructor(formService: FormService,
              userService: UserService,
              topicService: TopicService) {
    this.formService = formService;
    this.userService = userService;
    this.topicService = topicService;
  }

  ngOnInit() {
    this.subscriptionUser = this.userService.updateCurrentUser()
      .subscribe(result => {
        this.user = result;

        this.subscriptionForms = this.formService.getFormsByUserId(this.user.id)
          .subscribe(forms => {
            this.forms = forms;
          });
      });
  }

  ngOnDestroy() {
    if (this.subscriptionUser)
      this.subscriptionUser.unsubscribe();
    if (this.subscriptionForms)
      this.subscriptionForms.unsubscribe();
  }

  getActiveFieldCountOfForm(form: Form): number {
    let count = 0;
    for (let i = 0; i < form.fields.length; i++) {
      if (form.fields[i].active)
        count++;
    }
    return count;
  }
}
<table class="table-white-gray"
>
  <thead>
  <tr>
    <th class="w-5/12 px-3">Название</th>
    <th class="w-3/12 px-3">Тема</th>
    <th class="w-2/12 px-3">Вопросы (активных/неактивных)</th>
    <th class="w-1/12 px-3"></th>
    <th class="w-1/12 px-3"></th>
  </tr>
  </thead>
  <tbody>
  <tr *ngFor="let form of forms; let even = even" [ngClass]="{'bg-gray-100': even}">
    <td>{{form.name}}</td>
    <td>{{form.topic.shownName}}</td>
    <td>{{getActiveFieldCountOfForm(form)}}/{{form.fields.length}}</td>
    <td>
      <app-button-edit
        [editable]="form"
      ></app-button-edit>
    </td>
    <td>
      <app-button-delete
        [deletable]="form"
      ></app-button-delete>
    </td>
  </tr>
  </tbody>
</table>
