import {Component, Input} from '@angular/core';
import {User} from "../../entity/User";

@Component({
  selector: 'app-user-profile',
  templateUrl: './user-profile.component.html',
  styleUrls: ['./user-profile.component.css']
})
export class UserProfileComponent {
  @Input() user: User;

  isMale() : boolean {
    return this.user.gender.name == 'MALE';
  }

  isFemale() : boolean {
    return this.user.gender.name == 'FEMALE';
  }
}
<div class="profile-items">
  <div *ngIf="isMale()">
    <img class="user-profile-image" ngSrc="assets/images/user_male_icon.png" height="592" width="438">
  </div>
  <div *ngIf="isFemale()">
    <img class="user-profile-image" ngSrc="assets/images/user_female_icon.png" height="592" width="438">
  </div>

  <div class="flex flex-column items-center gap-y-3 justify-content-start">
    <p>
      <span class="user-description-title">Имя: </span>
      <span class="user-description-text">{{user.firstName}} {{user.lastName}}</span>
    </p>
  </div>
</div>
