import {Component, Input, OnDestroy, OnInit} from '@angular/core';
import {User} from "../../entity/User";
import {UserService} from "../../service/user.service";
import {Subscription} from "rxjs";

@Component({
  selector: 'app-user-profile',
  templateUrl: './user-profile.component.html',
  styleUrls: ['./user-profile.component.css']
})
export class UserProfileComponent implements OnInit, OnDestroy {
  @Input() user: User;

  private userService: UserService;
  private sessionUser: User;

  private subscription: Subscription;

  constructor(userService: UserService) {
    this.userService = userService;
  }

  ngOnInit() {
    this.subscription = this.userService.updateCurrentUser()
      .subscribe(result => {
        this.sessionUser = result;
      });
  }

  ngOnDestroy() {
    if (this.subscription)
      this.subscription.unsubscribe();
  }

  isMale() : boolean {
    return this.user.gender.name == 'MALE';
  }

  isFemale() : boolean {
    return this.user.gender.name == 'FEMALE';
  }

  isProfileOwn(): boolean {
    if (this.sessionUser && this.user) {
      return (this.sessionUser.id == this.user.id);
    } else {
      return false;
    }
  }
}
<app-middle-control>
  <div class="profile-and-button">
    <div class="profile-items">
      <div *ngIf="isMale()">
        <img class="user-profile-image" ngSrc="assets/images/user_male_icon.png" height="592" width="438">
      </div>
      <div *ngIf="isFemale()">
        <img class="user-profile-image" ngSrc="assets/images/user_female_icon.png" height="592" width="438">
      </div>

      <div class="flex flex-column justify-content-start">
        <p class="m-1">
          <span class="user-description-title">Имя: </span>
          <span class="user-description-text">{{user.firstName}} {{user.lastName}}</span>
        </p>
        <p class="m-1">
          <span class="user-description-title">Пол: </span>
          <span class="user-description-text">{{user.gender.shownName}}</span>
        </p>
        <p class="m-1">
          <span class="user-description-title">Email: </span>
          <span class="user-description-text">{{user.email}}</span>
        </p>
        <p class="m-1">
          <span class="user-description-title">Номер телефона: </span>
          <span class="user-description-text">+{{user.phoneNumber}}</span>
        </p>
        <p class="m-1">
          <span class="user-description-title">Форм создано: </span>
          <span class="user-description-text">{{user.forms.length}}</span>
        </p>
      </div>
    </div>

    <app-middle-control>
      <button
        class="button-dark"
        routerLink="/edit-my-profile"
      >Редактировать профиль</button>
    </app-middle-control>
  </div>
</app-middle-control>
