import {Component, EventEmitter, Input, OnInit, Output} from '@angular/core';
import {INamed} from "../../additional/INamed";

@Component({
  selector: 'app-combobox-group',
  templateUrl: './combobox-group.component.html',
  styleUrls: ['./combobox-group.component.css']
})
export class ComboboxGroupComponent implements OnInit {
  @Input() label: string;
  @Input() required: boolean;
  @Input() objects: INamed[];
  @Input() selected: INamed = null;

  @Output() itemSelection = new EventEmitter<INamed>();

  ngOnInit() {
    console.log('objects.length=' + this.objects.length);
    console.log('selected=' + this.selected);
  }

  selectionChanged(item: INamed) {
    this.selected = item;
    this.itemSelection.emit(item);
    console.log('selectionChanged(' + item.shownName + ') in combobox-group');
  }

  isChecked(item: INamed) {
    return !!this.selected && this.selected.shownName == item.shownName;
  }
}
<fieldset class="border-1 border-gray-500 rounded block">
  <legend>
    <app-field-name
      [fieldName]="label"
      [required]="required"
    ></app-field-name>
  </legend>

  <div class="flex items-center flex-row gap-x-1"
    *ngFor="let item of objects"
  >
    <input
      type="radio"
      name="{{label}}"
      value="{{item}}"
      (click)="selectionChanged(item)"
      [checked]="isChecked(item)"
    /><span class="text-black fs-6">{{item.shownName}}</span>
    <br/>
  </div>
</fieldset>
