import { Component } from '@angular/core';
import {UserService} from "../../service/user.service";
import {ModalService} from "../../service/modal.service";
import {TopicService} from "../../service/topic.service";

@Component({
  selector: 'app-topics-page',
  templateUrl: './topics-page.component.html',
  styleUrls: ['./topics-page.component.css']
})
export class TopicsPageComponent {
  loading = false;
  term: string = '';

  topicService: TopicService;
  modalService: ModalService;

  constructor(userService: TopicService, modalService: ModalService) {
    this.topicService = userService;
    this.modalService = modalService;
  }

  ngOnInit() {
    this.loading = true;

    this.topicService.getAll()
      .subscribe(() => {
        this.loading = false;
      });
  }
}
<div class="main-body">
  <app-header></app-header>
  <app-error></app-error>

  <app-large-area
    title="Разделы"
  >
    <input
      type="text"
      class="m-2 border rounded-1 py-2 px-4 w-11/12"
      placeholder="Поиск по темам..."
      [(ngModel)]="term"
    />

    <p *ngIf="loading" class="text-center text-lg p-2">Loading...</p>

    <app-user-profile
      *ngFor="let item of topicService.topics | filterUsers: term"
      [user]="item">
    </app-user-profile>

    <button
      class="rounded-pill bg-red-700 text-white fixed right-5 bottom-5 py-2 px-3"
      (click)="modalService.open()"
    >+</button>
  </app-large-area>
</div>

<app-modal-window
  *ngIf="modalService.isVisible()"
  title="Создать нового пользователя"
>
  <app-create-user></app-create-user>
</app-modal-window>
