import { Component } from '@angular/core';

@Component({
  selector: 'app-large-area',
  templateUrl: './large-area.component.html',
  styleUrls: ['./large-area.component.css']
})
export class LargeAreaComponent {

}
<div
  class="narrow-window"
>
  <app-logotype-text
    *ngIf="showLogo"
    [centered]="true"
  ></app-logotype-text>
  <h1 class="text-center mb-2 text-lg">{{title | titlecase}}</h1>
  <!--<app-light-h-line></app-light-h-line>-->
  <ng-content></ng-content>
</div>
