import {Component, OnInit} from '@angular/core';
import {User} from "./entity/User";
import {UserService} from "./service/users.service";
import {ModalService} from "./service/modal.service";

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent implements OnInit {
  title = 'angular-questionnaire';
  users: User[];
  loading = false;
  term: string = '';

  private userService: UserService;
  modalService: ModalService;

  constructor(userService: UserService, modalService: ModalService) {
    this.userService = userService;
    this.modalService = modalService;
  }

  ngOnInit() {
    this.loading = true;

    this.userService.getAll().subscribe(users => {
      this.users = users;

      this.loading = false;
    });
  }
}
<div class="max-w-2xl mx-auto bg-blue-50">
  <app-header></app-header>
  <app-error></app-error>

  <input
    type="text"
    class="m-2 border rounded-1 py-2 px-4 w-11/12"
    placeholder="Поиск по пользователям..."
    [(ngModel)]="term"
  />

  <p *ngIf="loading" class="text-center text-lg p-2">Loading...</p>

  <app-user-profile
    *ngFor="let item of users | filterUsers: term"
    [user]="item">
  </app-user-profile>

  <app-modal-create-user
    *ngIf="modalService.isVisible()"
    title="Создать нового пользователя"
  >
    <app-create-user></app-create-user>
  </app-modal-create-user>
</div>
