import { Component } from '@angular/core';
import {ModalService} from "../../service/modal.service";
import {FormService} from "../../service/form.service";
import {ActivatedRoute} from "@angular/router";
import {TopicService} from "../../service/topic.service";
import {Topic} from "../../entity/Topic";

@Component({
  selector: 'app-forms-page',
  templateUrl: './forms-page.component.html',
  styleUrls: ['./forms-page.component.css']
})
export class FormsPageComponent {
  private activatedRoute: ActivatedRoute;

  loading = false;
  private formsLoaded = false;
  private topicLoaded = false;

  term: string = '';

  formService: FormService;
  topicService: TopicService;
  modalService: ModalService;

  topic: Topic;

  constructor(formService: FormService,
              topicService: TopicService,
              modalService: ModalService,
              activatedRoute: ActivatedRoute) {
    this.formService = formService;
    this.topicService = topicService;
    this.modalService = modalService;
    this.activatedRoute = activatedRoute;
  }

  ngOnInit() {
    this.activatedRoute.queryParams.subscribe(params => {
      let topicId = params['topic_id'];

      this.loading = true;

      this.topicService.getTopicById(topicId).subscribe(() => {
        this.topicLoaded = true;
        this.checkLoading();
      });

      this.formService.getFormsByTopicId(topicId)
        .subscribe(() => {
          this.formsLoaded = true;
          this.checkLoading();
        });
    });
  }

  private checkLoading() {
    if (this.formsLoaded || this.topicLoaded)
      this.loading = false;
  }
}
<div class="main-body">
  <app-header></app-header>
  <app-error></app-error>

  <app-large-area
    title="Разделы"
  >
    <input
      type="text"
      class="m-2 border rounded-1 py-2 px-4 w-11/12"
      placeholder="Поиск по темам..."
      [(ngModel)]="term"
    />

    <p *ngIf="loading" class="text-center text-lg p-2">Loading...</p>

    <app-topic-item
      *ngFor="let item of topicService.topics | filterTopics: term"
      [topic]="item">
    </app-topic-item>

    <button
      class="rounded-pill bg-red-700 text-white fixed right-5 bottom-8 py-2 px-3"
      (click)="modalService.open()"
    >+</button>
  </app-large-area>
</div>

<app-modal-window
  *ngIf="modalService.isVisible()"
  title="Создать новую тему"
>
  <app-create-topic></app-create-topic>
</app-modal-window>
