import { Component } from '@angular/core';
import {HttpClient} from "@angular/common/http";
import {User} from "./entity/User";

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {
  title = 'angular-questionnaire';

  private http: HttpClient;
  user: User;

  constructor(http: HttpClient) {
    this.http = http;

    this.http.get<User>("http://localhost:8080/users/1").subscribe(result => {
      this.user = result;
      console.log(this.user);
    });
  }
}
<div class="max-w-2xl container mx-auto bg-blue-50">
  <app-header></app-header>
  <h1 class="font-bold text-3xl text-fuchsia-900 text-center">{{user.email}}</h1>
</div>
