import {Component, Input} from '@angular/core'
import {User} from "../../entity/User";

@Component({
  selector: 'app-user-row',
  templateUrl: './user-row.component.html',
  styleUrls: ['./user-row.component.css']
})
export class UserRowComponent {
  @Input() user: User;

  isMale() : boolean {
    return this.user.gender.name == 'MALE';
  }

  isFemale() : boolean {
    return this.user.gender.name == 'FEMALE';
  }
}
<a [routerLink]="['/user-profiles', user.id]" xmlns="http://www.w3.org/1999/html" class="text-decoration-none">
  <div class="user-row-div">
    <div *ngIf="isMale()">
      <img class="user-profile-image" ngSrc="assets/images/user_male_icon.png" height="592" width="438">
    </div>
    <div *ngIf="isFemale()">
      <img class="user-profile-image" ngSrc="assets/images/user_female_icon.png" height="592" width="438">
    </div>

    <div class="user-name-email-div">
      <span class="text-black">{{user.firstName}} {{user.lastName}} </span>
      <span class="font-monospace text-blue-800 font-medium">({{user.email}})</span>
    </div>
  </div>
</a>
