import { Component } from '@angular/core';
import {UserService} from "../../service/user.service";
import {ModalService} from "../../service/modal.service";

@Component({
  selector: 'app-user-list',
  templateUrl: './user-list.component.html',
  styleUrls: ['./user-list.component.css']
})
export class UserListComponent {
  title = 'angular-questionnaire';
  loading = false;
  term: string = '';

  userService: UserService;
  modalService: ModalService;

  constructor(userService: UserService, modalService: ModalService) {
    this.userService = userService;
    this.modalService = modalService;
  }

  ngOnInit() {
    this.loading = true;

    this.userService.getAll()
      .subscribe(() => {
        this.loading = false;
      });
  }
}
<div class="main-body">
  <app-header></app-header>
  <app-error></app-error>

  <app-large-area
    title="Пользователи сайта"
  >
    <input
      type="text"
      class="m-2 border rounded-1 py-2 px-4 w-11/12"
      placeholder="Поиск по пользователям..."
      [(ngModel)]="term"
    />

    <p *ngIf="loading" class="text-center text-lg p-2">Loading...</p>

    <app-user-profile
      *ngFor="let item of userService.users | filterUsers: term"
      [user]="item">
    </app-user-profile>

    <button
      class="rounded-pill bg-red-700 text-white right-5 bottom-5 py-2 px-3"
      (click)="modalService.open()"
    >+</button>
  </app-large-area>
</div>

<app-modal-window
  *ngIf="modalService.isVisible()"
  title="Создать нового пользователя"
>
  <app-create-user></app-create-user>
</app-modal-window>
