import {Component, OnDestroy, OnInit} from '@angular/core';
import {ActivatedRoute} from "@angular/router";
import {Subscription} from "rxjs";
import {Field} from "../../entity/Field";
import {FieldService} from "../../service/field.service";
import {FormService} from "../../service/form.service";
import {Form} from "../../entity/Form";
import {ModalCreateWindowService} from "../../service/modal-create-window.service";
import {ModalEditWindowService} from "../../service/modal-edit-window.service";
import {ModalDeleteWindowService} from "../../service/modal-delete-window.service";

@Component({
  selector: 'app-my-fields-page',
  templateUrl: './my-fields-page.component.html',
  styleUrls: ['./my-fields-page.component.css']
})
export class MyFieldsPageComponent implements OnInit, OnDestroy {
  private activatedRoute: ActivatedRoute;
  private fieldService: FieldService;
  private formService: FormService;

  modalCreateWindow: ModalCreateWindowService;
  modalEditWindow: ModalEditWindowService;
  modalDeleteWindow: ModalDeleteWindowService;

  form: Form;
  fields: Field[];

  private subscriptionParams: Subscription;
  private subscriptionFields: Subscription;
  private subscriptionForm: Subscription;

  constructor(activatedRoute: ActivatedRoute,
              fieldService: FieldService,
              formService: FormService,
              modalCreateWindow: ModalCreateWindowService,
              modalEditWindow: ModalEditWindowService,
              modalDeleteWindow: ModalDeleteWindowService) {
    this.activatedRoute = activatedRoute;
    this.fieldService = fieldService;
    this.formService = formService;
  }

  ngOnInit() {
    this.subscriptionParams = this.activatedRoute.params
      .subscribe(params => {
          let formId: bigint = params['id'];

          this.subscriptionFields = this.fieldService.getFieldsByFormId(formId)
            .subscribe(result => {
              this.fields = result;
            });

          this.subscriptionForm = this.formService.getFormById(formId)
            .subscribe(result => {
              this.form = result;
            });
        }
      );
  }

  ngOnDestroy() {
    if (this.subscriptionParams)
      this.subscriptionParams.unsubscribe();
    if (this.subscriptionFields)
      this.subscriptionFields.unsubscribe();
    if (this.subscriptionForm)
      this.subscriptionForm.unsubscribe();
  }
}
<div class="main-body">
  <app-header></app-header>
  <app-error></app-error>

  <app-large-area-with-open-modal-button
    title="Поля"
  >
    <app-manage-questionnaires></app-manage-questionnaires>
  </app-large-area-with-open-modal-button>
</div>

<app-modal-create-window
  *ngIf="modalCreateWindow.isVisible()"
  title="Создать форму"
>
  <app-create-form-choose-topic></app-create-form-choose-topic>
</app-modal-create-window>

<app-modal-edit-window
  *ngIf="modalEditWindow.isVisible()"
  title="Редактировать форму"
>
  <app-edit-form
  ></app-edit-form>
</app-modal-edit-window>

<app-modal-delete-window
  *ngIf="modalDeleteWindow.isVisible()"
  title="Удалить форму"
>
  <app-delete-form></app-delete-form>
</app-modal-delete-window>
