import {Component, EventEmitter, Input, OnInit, Output} from '@angular/core';
import {FieldOption} from "../../entity/FieldOption";
import {FormArray, FormControl, FormGroup, Validators} from "@angular/forms";

@Component({
  selector: 'app-option-list',
  templateUrl: './option-list.component.html',
  styleUrls: ['./option-list.component.css']
})
export class OptionListComponent implements OnInit {
  @Input() fieldOptions: FieldOption[] = [];
  @Input() visible: boolean = false;

  @Output() fieldOptionsChange = new EventEmitter<FieldOption[]>();

  fieldOptionsForm: FormGroup;

  constructor() {
    this.fieldOptionsForm = new FormGroup({
      options: new FormArray([
        new FormGroup({
          text: new FormControl<string>('', [
            Validators.required,
            Validators.maxLength(200)
          ])
        })
      ])
    });
  }

  ngOnInit() {
    this.populateFieldOptionsForm();
  }

  getOptionsFormArray(): FormArray {
    return this.fieldOptionsForm.get('options') as FormArray;
  }

  private populateFieldOptionsForm() {
    const optionsArray = this.getOptionsFormArray();
    optionsArray.clear();

    for (let i = 0; i < this.fieldOptions.length; i++) {
      this.addFieldOption(this.fieldOptions[i].text);
    }
  }

  addFieldOption(value: string = '') {
    const optionsArray = this.getOptionsFormArray();
    optionsArray.push(
      new FormGroup({
        text: new FormControl<string>(value, [
          Validators.required,
          Validators.maxLength(200)
        ])
      })
    );

    this.fieldOptions.push(new FieldOption(null, ''));
  }

  removeFieldOption(indexToRemove: number) {
    const optionsArray = this.getOptionsFormArray();
    optionsArray.removeAt(indexToRemove);

    this.fieldOptions = this.fieldOptions.filter((_, index) => index != indexToRemove);
  }

  updateTextInFieldOptions() {
    const optionsArray = this.getOptionsFormArray();

    for (let i = 0; i < this.fieldOptions.length; i++) {
      let text = optionsArray.controls[i].getRawValue().text;

      this.fieldOptions[i].text = text;
      this.fieldOptions[i].shownName = text;
    }

    this.fieldOptionsChange.emit(this.fieldOptions);

    console.log('fieldOptions (info from option-list):');
    this.fieldOptions.forEach(option => console.log(option.text + ', ' + option.id));
  }
}
<div *ngIf="visible">
  <br/>
  <p class="mt-2 mb-0">
    <app-field-name
      fieldName="Варианты ответа"
      [required]="true"
    ></app-field-name>
  </p>

  <div class="options-container">
    <form [formGroup]="fieldOptionsForm">
      <div formArrayName="options">
        <div *ngFor="let child of fieldOptionsForm.get('options')['controls']; let i = index"
             formGroupName="{{i}}"
        >
          <div class="row mt-1">
            <div class="col">
              <input type="text" (change)="updateTextInFieldOptions()" formControlName="text"
                     class="form-control text-white" placeholder="Вариант {{i + 1}}">
            </div>
            <div class="col-auto">
              <button type="button" class="btn btn-danger text-white text-xl fw-bolder" (click)="removeFieldOption(i)">✕</button>
            </div>
          </div>
        </div>
      </div>
    </form>
  </div>
  <button type="button" class="btn btn-primary mt-2 text-white text-xl fw-semibold" (click)="addFieldOption()">Добавить</button>
</div>
