@Component({
  selector: 'app-modal-create-user',
  templateUrl: './modal-create-user.component.html',
  styleUrls: ['./modal-create-user.component.css']
})
export class ModalCreateUserComponent {
  @Input() title: string;
}
<div class="bg-black/50 fixed top-0 right-0 left-0 bottom-0">
</div>

<div
  class="w-[500px] absolute top-1/4 rounded p-6 bg-white left-1/2 -translate-x-1/2"
>
  <h1 class="text-center mb-2 text-lg">{{title | titlecase}}</h1>
  <svg class="stroke-current text-gray-500 inline-block h-2 w-full" viewBox="0 0 w-full 2" xmlns="http://www.w3.org/2000/svg" fill="none" stroke-width="1" stroke-linecap="round" stroke-linejoin="round">
