import {Component, Input} from '@angular/core';

@Component({
  selector: 'app-narrow-area',
  templateUrl: './narrow-area.component.html',
  styleUrls: ['./narrow-area.component.css']
})
export class NarrowAreaComponent {
  @Input() title: string;
}
<div
  class="w-[500px] position-static rounded top-16 p-6 bg-white left-1/2 -translate-x-1/2"
>
  <h1 class="text-center mb-2 text-lg">{{title | titlecase}}</h1>
  <!--<app-light-h-line></app-light-h-line>-->
  <ng-content></ng-content>
</div>
