import {Component, OnInit} from '@angular/core';
import {FormControl, FormGroup} from "@angular/forms";

@Component({
  selector: 'app-create-user',
  templateUrl: './create-user.component.html',
  styleUrls: ['./create-user.component.css']
})
export class CreateUserComponent implements OnInit{
  form = new FormGroup({
    email: new FormControl<string>(''),
    password: new FormControl<string>(''),
    confirmPassword: new FormControl<string>(''),
    firstName: new FormControl<string>(''),
    lastName: new FormControl<string>(''),
    phoneNumber: new FormControl<string>('')
  })

  constructor() {
  }

  ngOnInit() {
  }

  submit() {
    console.log(this.form.value);
  }
}
<form [formGroup]="form" (ngSubmit)="submit()">
  <app-text-field-placeholder formControlName="" placeholder="Email" [required]="true"></app-text-field-placeholder>
  <app-text-field fieldName="Пароль" [required]="true" [nextLine]="false"></app-text-field>
  <app-text-field fieldName="Подтверждение пароля" [required]="true" [nextLine]="false"></app-text-field>
  <app-text-field fieldName="Имя" [required]="true" [nextLine]="false"></app-text-field>
  <app-text-field fieldName="Фамилия" [required]="true" [nextLine]="false"></app-text-field>
  <app-text-field fieldName="Номер телефона" [required]="false" [nextLine]="false"></app-text-field>

  <button
    type="submit"
    class="py-2 px-4 mt-3 mx-auto rounded-1 bg-blue-600 text-white"
  >Создать</button>
</form>
