import { Component } from '@angular/core';
import {HttpClient} from "@angular/common/http";
import {User} from "./entity/User";

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {
  private http: HttpClient;

  title = 'angular-questionnaire';
  users: User[];

  constructor(http: HttpClient) {
    this.http = http;
    this.subscribeForUserData();
  }

  private subscribeForUserData() {
    this.http.get<User[]>("http://localhost:8080/users").subscribe(result => {
      this.users = result;
    });
  }
}
<div class="max-w-2xl mx-auto bg-blue-50">
  <app-header></app-header>
  <app-user-profile [user]="users[0]"></app-user-profile>
  <app-user-profile [user]="users[1]"></app-user-profile>
</div>

<div *ngIf="users[0].gender.name === 'MALE'">
  <img class="user-profile-image" src="assets/images/user_male_icon.png">
</div>
<div *ngIf="users[0].gender.name === 'FEMALE'">
  <img class="user-profile-image" src="assets/images/user_female_icon.png">
</div>
