import {Component, OnInit} from '@angular/core';
import {User} from "../../entity/User";
import {UserService} from "../../service/user.service";
import {Router} from "@angular/router";
import {SessionService} from "../../service/session.service";

@Component({
  selector: 'app-navigation-menu',
  templateUrl: './navigation-menu.component.html',
  styleUrls: ['./navigation-menu.component.css']
})
export class NavigationMenuComponent implements OnInit {
  private userService: UserService;
  sessionService: SessionService;
  private router: Router;

  user: User;

  constructor(userService: UserService,
              sessionService: SessionService,
              router: Router) {
    this.userService = userService;
    this.sessionService = sessionService;
    this.router = router;
  }


  ngOnInit(): void {
    this.userService.updateCurrentUser()
      .subscribe(result => {
        this.user = result;
      });
  }

  getUserName(): string {
    if (this.userService.isUserPresent(this.user))
      return this.user.firstName + ' ' + this.user.lastName;
    return 'Мой профиль';
  }

  logOut() {
    this.userService.logOut();
    this.doTransferToMainPage();
  }

  doTransferToMainPage() {
    this.router.navigate(['/']);
  }
}
<nav class="navigation_menu"
>
  <h1>
    <a routerLink="/" class="text-decoration-none">
      <app-logotype-text></app-logotype-text>
    </a>
  </h1>

  <div class="menu-items-div"
  >
    <div class="menu-items-in-row">
      <a routerLink="/topics" class="menu-item">Каталог</a>
      <a *ngIf="sessionService.isCurrentUserAuthorized()" routerLink="/" class="menu-item">Мои формы</a>
      <a *ngIf="sessionService.isCurrentUserAdmin()" routerLink="/user-list" class="menu-item">Пользователи</a>
    </div>

    <div *ngIf="sessionService.isCurrentUserAuthorized()"
         class="dropdown"
    >
      <button class="dropbtn">{{getUserName()}} ▼</button>
      <div class="dropdown-content">
        <a href="">Мой профиль</a>
        <a href="">Сменить пароль</a>
        <a href="" (click)="logOut()">Выйти</a>
      </div>
    </div>
  </div>
</nav>
