import { Component } from '@angular/core';

@Component({
  selector: 'app-create-user',
  templateUrl: './create-user.component.html',
  styleUrls: ['./create-user.component.css']
})
export class CreateUserComponent {

}
<form>
  <app-text-field fieldName="Email" [required]="true" [nextLine]="true"></app-text-field>
  <app-text-field fieldName="Пароль" [required]="true" [nextLine]="true"></app-text-field>
  <app-text-field fieldName="Подтверждение пароля" [required]="true" [nextLine]="true"></app-text-field>
  <app-text-field fieldName="Имя" [required]="true" [nextLine]="true"></app-text-field>
  <app-text-field fieldName="Фамилия" [required]="true" [nextLine]="true"></app-text-field>
  <app-text-field fieldName="Номер телефона" [required]="false" [nextLine]="false"></app-text-field>

  <button
    type="submit"
    class="py-2 px-4 mt-3 mx-auto rounded-1 bg-blue-600 text-white"
  >Создать</button>
</form>
