import {Component, OnDestroy, OnInit} from '@angular/core';
import {User} from "../../entity/User";
import {UserService} from "../../service/user.service";
import {ActivatedRoute} from "@angular/router";
import {Subscription} from "rxjs";

@Component({
  selector: 'app-user-profile-page',
  templateUrl: './user-profile-page.component.html',
  styleUrls: ['./user-profile-page.component.css']
})
export class UserProfilePageComponent implements OnInit, OnDestroy {
  private activatedRoute: ActivatedRoute;
  private userService: UserService;

  user: User;

  private subscriptionParams: Subscription;
  private subscriptionUser: Subscription;

  constructor(activatedRoute: ActivatedRoute,
              userService: UserService) {
    this.activatedRoute = activatedRoute;
    this.userService = userService;
  }

  ngOnInit(): void {
    this.subscriptionParams = this.activatedRoute.params
      .subscribe(params => {
        let userId: bigint = params['id'];

        this.subscriptionUser = this.userService.getUserById(userId)
          .subscribe(result => {
            this.user = result;
          })
      });
  }

  ngOnDestroy(): void {
    if (this.subscriptionParams)
      this.subscriptionParams.unsubscribe();
  }

  getUserName(): string {
    if (this.userService.isUserPresent(this.user))
      return this.user.firstName + ' ' + this.user.lastName;
    return '';
  }
}
<div class="main-body">
  <app-header></app-header>
  <app-error></app-error>

  <app-large-area
    title="Профиль пользователя {{getUserName()}}"
  >
    <app-user-info
      [user]="user"
    ></app-user-info>
  </app-large-area>
</div>
