import {Component, Input} from '@angular/core';
import {Router} from "@angular/router";
import {UrlService} from "../../service/url.service";

@Component({
  selector: 'app-large-area-with-open-modal-button',
  templateUrl: './large-area-with-open-modal-button.component.html',
  styleUrls: ['./large-area-with-open-modal-button.component.css']
})
export class LargeAreaWithOpenModalButtonComponent {
  @Input() title: string;
  @Input() buttonBackRoute: string;

  private router: Router;
  private urlService: UrlService;

  constructor(router: Router,
              urlService: UrlService) {
    this.router = router;
    this.urlService = urlService;
  }

  navigateBack() {
    this.router.navigate([this.buttonBackRoute]);
  }
}
<div
  class="large-window"
>
  <div class="row">
    <div class="col-auto">
      <button *ngIf="buttonBackRoute"
              type="submit"
              class="button-back-arrow"
              (click)="navigateBack()"
      >ü†î</button>
    </div>
    <div class="col">
      <h1 class="text-center fs-4">{{title | titlecase}}</h1>
    </div>
    <div class="col-auto">
      <app-button-add-new></app-button-add-new>
    </div>
  </div>
  <app-dark-h-line></app-dark-h-line>
  <ng-content></ng-content>
</div>
