>
  <thead>
  <tr>
    <th class="w-7/12">Название</th>
    <th class="w-3/12">Тема</th>
    <th class="w-auto">Поля</th>
    <th class="w-auto"></th>
  </tr>
  </thead>
  <tbody *ngIf="forms && fieldCounts">
  <tr *ngFor="let form of forms; let even = even; let i = index" [ngClass]="{'bg-gray-100': even, 'cursor-pointer': true}"
      (click)="getRouterLinkForForm(i)"
  >
    <td>{{form.name}}</td>
    <td>{{form.topic.shownName}}</td>
    <td>{{fieldCounts[i]}}</td>
    <td>
      <div class="action-buttons">
        <app-button-edit
          [editable]="form"
        ></app-button-edit>
        <app-button-delete
          [deletable]="form"
        ></app-button-delete>
      </div>
    </td>
  </tr>
  </tbody>
