import { Injectable } from '@angular/core';
import {Router} from "@angular/router";

@Injectable({
  providedIn: 'root'
})
export class UrlService {
  private router: Router;

  private LAST_PAGE_KEY = 'last_page_route';
  private CURRENT_PAGE_KEY = 'current_page_route';

  constructor(router: Router) {
    this.router = router;
  }

  saveCurrentPage() {
    let currentPage = this.getCurrentUrl();

    let prevPage = this.getCurrentPage();
    if (prevPage != currentPage) {
      sessionStorage.setItem(this.LAST_PAGE_KEY, prevPage);
      sessionStorage.setItem(this.CURRENT_PAGE_KEY, currentPage);
    }

    console.log('prev page: ' + prevPage);
    console.log('current page: ' + currentPage);
    console.log('last page: ' + this.getLastPage());
  }

  getCurrentPage() {
    return sessionStorage.getItem(this.CURRENT_PAGE_KEY);
  }

  getLastPage() {
    return sessionStorage.getItem(this.LAST_PAGE_KEY);
  }

  private getCurrentUrl(): string {
    const urlTree = this.router.createUrlTree([], {
      queryParamsHandling: 'merge',
      preserveFragment: true
    });

    return this.router.serializeUrl(urlTree);
  }
}
