import {Component, Input} from '@angular/core';
import {Form} from "../../entity/Form";

@Component({
  selector: 'app-form-item-in-list',
  templateUrl: './form-item-in-list.component.html',
  styleUrls: ['./form-item-in-list.component.css']
})
export class FormItemInListComponent {
  @Input() form: Form;
}
<div class="py-2 px-4 flex items-center flex-column gap-x-4">
  <h2 class="text-black fs-5 text-center">
    {{form.name}}
  </h2>

  <button class="button-red-very-rounded-narrow mt-2"
  >
    Пройти
  </button>

  <br/>
  <span class="font-normal text-black text-center">
    * * *
  </span>
</div>
