import {Component, Input} from '@angular/core';

@Component({
  selector: 'app-logotype-text',
  templateUrl: './logotype-text.component.html',
  styleUrls: ['./logotype-text.component.css']
})
export class LogotypeTextComponent {
  @Input() centered: boolean = false;
}
<span *ngIf="centered"
  class="mx-auto"
>
  <span class="font-bold text-4xl text-black">Quest</span>
  <span class="font-bold text-4xl text-red-500">i</span>
  <span class="font-bold text-4xl text-black">onna</span>
  <span class="font-bold text-4xl text-red-500">i</span>
  <span class="font-bold text-4xl text-black">re</span>
</span>
<span *ngIf="!centered">
  <span class="font-bold text-4xl text-black">Quest</span>
  <span class="font-bold text-4xl text-red-500">i</span>
  <span class="font-bold text-4xl text-black">onna</span>
  <span class="font-bold text-4xl text-red-500">i</span>
  <span class="font-bold text-4xl text-black">re</span>
</span>
