import {Component, OnInit} from '@angular/core';
import {Gender} from "../../entity/Gender";

@Component({
  selector: 'app-create-user',
  templateUrl: './create-user.component.html',
  styleUrls: ['./create-user.component.css']
})
export class CreateUserComponent implements OnInit {
  email: string = '123';
  password: string;
  confirmPassword: string;
  firstName: string;
  lastName: string;
  phoneNumber: string;
  gender: Gender;

  constructor() {
  }

  ngOnInit() {
  }

  submit() {

  }
}
<form (ngSubmit)="submit()">
  <app-text-field-placeholder
    placeholder="Email"
    [required]="true"
    requestParam="email"
    [(ngModel)]="email"
  ></app-text-field-placeholder>

  <app-text-field
    fieldName="Пароль"
    [required]="true"
    [nextLine]="false"
    requestParam="password"
  ></app-text-field>

  <app-text-field
    fieldName="Подтверждение пароля"
    [required]="true"
    [nextLine]="false"
  ></app-text-field>

  <app-text-field
    fieldName="Имя"
    [required]="true"
    [nextLine]="false"
    requestParam="firstName"
  ></app-text-field>

  <app-text-field
    fieldName="Фамилия"
    [required]="true"
    [nextLine]="false"
    requestParam="lastName"
  ></app-text-field>

  <app-text-field
    fieldName="Номер телефона"
    [required]="false"
    [nextLine]="false"
    requestParam="phoneNumber"
  ></app-text-field>

  <button
    type="submit"
    class="py-2 px-4 mt-3 mx-auto rounded-1 bg-blue-600 text-white"
  >Создать</button>
</form>
