import { Component } from '@angular/core';
import {UserService} from "../../service/user.service";
import {ModalService} from "../../service/modal.service";
import {TopicService} from "../../service/topic.service";
import {Topic} from "../../entity/Topic";
import {SessionService} from "../../service/session.service";

@Component({
  selector: 'app-topics-page',
  templateUrl: './topics-page.component.html',
  styleUrls: ['./topics-page.component.css']
})
export class TopicsPageComponent {
  loading = false;
  term: string = '';

  topicService: TopicService;
  modalService: ModalService;
  sessionService: SessionService;

  topics: Topic[];

  constructor(topicService: TopicService,
              modalService: ModalService,
              sessionService: SessionService) {
    this.topicService = topicService;
    this.modalService = modalService;
    this.sessionService = sessionService;
  }

  ngOnInit() {
    this.loading = true;

    this.topicService.getAll()
      .subscribe(result => {
        this.topics = result;
        this.loading = false;
      });
  }
}
<div class="main-body">
  <app-header></app-header>
  <app-error></app-error>

  <app-large-area
    title="Разделы"
  >
    <input
      type="text"
      class="m-2 border rounded-1 py-2 px-4 w-11/12"
      placeholder="Поиск по темам..."
      [(ngModel)]="term"
    />

    <p *ngIf="loading" class="text-center text-lg p-2">Loading...</p>

    <app-topic-item
      *ngFor="let item of topics | filterTopics: term"
      [topic]="item">
    </app-topic-item>

    <button *ngIf=""
      class="rounded-pill bg-red-700 text-white fixed right-5 bottom-8 py-2 px-3"
      (click)="modalService.open()"
    >+</button>
  </app-large-area>
</div>

<app-modal-window
  *ngIf="modalService.isVisible()"
  title="Создать новую тему"
>
  <app-create-topic></app-create-topic>
</app-modal-window>
