import {Component, Input} from '@angular/core';
import {Topic} from "../../entity/Topic";
import {NavigationExtras, Router} from "@angular/router";

@Component({
  selector: 'app-topic-item',
  templateUrl: './topic-item.component.html',
  styleUrls: ['./topic-item.component.css']
})
export class TopicItemComponent {
  @Input() topic: Topic;

  private router: Router;

  constructor(router: Router) {
    this.router = router;
  }

  doTransferToFormsOfTopicPage() {
    this.router.navigate(['/forms-by-topic', this.topic.id]);
  }
}
<div class="py-2 px-4 flex items-center flex-column gap-x-4">
  <h2 class="text-black fs-5 text-center">
    {{topic.name}}
  </h2>
  <p class="text-sm text-gray-500 text-center border-2">
    {{topic.description}}
  </p>

  <button class="button-red-very-rounded-narrow"
    (click)="doTransferToFormsOfTopicPage()"
  >
    Открыть
  </button>

  <br/>
  <span class="font-normal text-black text-center">
    * * *
  </span>
</div>
